#{prodNo}
seq_product_prod_no.nextval , #{prodName: VARCHAR}, #{prodDetail: VARCHAR}, #{manuDate: DATE}, #{price: NUMERIC}, #{fileName: VARCHAR},
#{regDate: DATE}
SYSDATE , #{stock: NUMERIC}, #{category.categoryNo: NUMERIC} )

mapper.select:
SELECT p.prod_no, p.prod_name, p.prod_detail, p.manufacture_day, p.price, p.image_file, p.reg_date, p.stock FROM product p LEFT OUTER JOIN category c ON c.category_no = p.category_no
prod_no IN
#{id}

SELECT v.prod_no AS "prod_no", v.prod_name AS "prod_name", v.prod_detail AS "prod_detail", v.manufacture_day AS "manufacture_day", v.price AS "price", v.image_file AS "image_file", v.reg_date AS "reg_date", v.stock AS "stock", v.category_no AS "category_no", v.category_name AS "category_name", v.count AS "count" FROM ( SELECT ROW_NUMBER() OVER(ORDER BY p.prod_no ASC) row_num, p.prod_no prod_no, p.prod_name prod_name, p.prod_detail prod_detail, p.manufacture_day manufacture_day, p.price price, p.image_file image_file, p.reg_date reg_date, p.stock stock, c.category_no category_no, c.category_name category_name, COUNT(*) OVER() count FROM product p LEFT OUTER JOIN category c ON c.category_no = p.category_no
p.prod_name LIKE #{prodName}
p.price BETWEEN #{lowerBound} AND #{upperBound}
p.price $gt;= #{lowerBound}
p.price $lt;= #{upperBound}
AND c.category_no = #{categoryNo}
ORDER BY p.reg_date DESC -- Should be flexible ) v WHERE v.row_num BETWEEN #{startRowNum} AND #{endRowNum}
